import { useState, useRef, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  IconButton,
  Box,
  Paper,
  Typography,
  Avatar,
  Stack
} from '@mui/material';
import { Send as SendIcon, Close as CloseIcon } from '@mui/icons-material';
import { useQuery, useMutation } from '@tanstack/react-query';

interface ChatDialogProps {
  open: boolean;
  onClose: () => void;
  projectId: string;
  recipientId: string;
}

function ChatDialog({ open, onClose, projectId, recipientId }: ChatDialogProps) {
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);
  
  // Mesajları çek
  const { data: messages = [], refetch } = useQuery({
    queryKey: ['messages', projectId, recipientId],
    queryFn: async () => {
      const response = await fetch(`/api/messages/${projectId}/${recipientId}`);
      if (!response.ok) throw new Error('Mesajlar yüklenemedi');
      return response.json();
    },
  });

  // Yeni mesaj gönder
  const { mutate: sendMessage } = useMutation({
    mutationFn: async (content: string) => {
      const response = await fetch('/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ projectId, recipientId, content }),
      });
      if (!response.ok) throw new Error('Mesaj gönderilemedi');
      return response.json();
    },
    onSuccess: () => {
      setNewMessage('');
      refetch();
    },
  });

  // Otomatik scroll
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = (e: React.FormEvent) => {
    e.preventDefault();
    if (newMessage.trim()) {
      sendMessage(newMessage);
    }
  };

  return (
    <Dialog 
      open={open} 
      onClose={onClose}
      maxWidth="sm"
      fullWidth
    >
      <DialogTitle sx={{ m: 0, p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h6">Mesajlar</Typography>
        <IconButton onClick={onClose}>
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent sx={{ height: '60vh', display: 'flex', flexDirection: 'column' }}>
        {/* Mesaj Listesi */}
        <Box sx={{ flexGrow: 1, overflow: 'auto', mb: 2 }}>
          <Stack spacing={1}>
            {messages.map((message: ChatMessage) => (
              <Box
                key={message.id}
                sx={{
                  display: 'flex',
                  justifyContent: message.isSender ? 'flex-end' : 'flex-start',
                }}
              >
                <Paper
                  sx={{
                    p: 1,
                    maxWidth: '70%',
                    bgcolor: message.isSender ? 'primary.main' : 'grey.100',
                    color: message.isSender ? 'white' : 'text.primary',
                    borderRadius: 2,
                  }}
                >
                  <Typography variant="body1">{message.content}</Typography>
                  <Typography variant="caption" sx={{ opacity: 0.7 }}>
                    {new Date(message.createdAt).toLocaleTimeString()}
                  </Typography>
                </Paper>
              </Box>
            ))}
            <div ref={messagesEndRef} />
          </Stack>
        </Box>

        {/* Mesaj Gönderme Formu */}
        <Box component="form" onSubmit={handleSend} sx={{ display: 'flex', gap: 1 }}>
          <TextField
            fullWidth
            size="small"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Mesajınızı yazın..."
            variant="outlined"
          />
          <IconButton 
            color="primary" 
            type="submit" 
            disabled={!newMessage.trim()}
          >
            <SendIcon />
          </IconButton>
        </Box>
      </DialogContent>
    </Dialog>
  );
}

export default ChatDialog;
