import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { Box, Typography, CircularProgress, Card, CardContent, Button, Avatar, Chip } from '@mui/material';
import api from '../utils/axios';
import { MessageOutlined as MessageIcon } from '@mui/icons-material';
import ChatDialog from '../components/ChatDialog';

interface Project {
  id: number;
  title: string;
  description: string;
  budget: number;
  deadline: string;
  category: string;
  customer_name: string;
  customer_image?: string;
  status: string;
  created_at: string;
  userId: string;
  customer_id: string;
}

const ProjectDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [project, setProject] = useState<Project | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();
  const [isChatOpen, setIsChatOpen] = useState(false);
  const location = useLocation();
  const [recipientId, setRecipientId] = useState('');

  useEffect(() => {
    const fetchProjectDetail = async () => {
      if (!id) {
        setError("Geçersiz proje id.");
        setLoading(false);
        return;
      }
      try {
        setLoading(true);
        const response = await api.get(`/projects/${id}`);
        
        if (response.data?.success) {
          const projectData = response.data.project;
          
          if (!projectData.customer_id) {
            throw new Error('Proje sahibi bilgisi bulunamadı');
          }
          
          setProject({
            ...projectData,
            customer_id: projectData.customer_id,
            budget: parseFloat(projectData.budget),
            deadline: new Date(projectData.deadline).toISOString(),
            created_at: new Date(projectData.created_at).toISOString()
          });
        } else {
          setError("Proje bulunamadı.");
        }
      } catch (err: any) {
        console.error("Project detail fetch error:", err);
        setError(err.response?.data?.message || "Proje yüklenirken hata oluştu.");
      } finally {
        setLoading(false);
      }
    };

    fetchProjectDetail();
  }, [id]);

  useEffect(() => {
    if (location.state?.openChat && project?.customer_id) {
      const calculatedRecipientId = location.state.recipientId || project.customer_id.toString();
      console.log('RecipientID Ayarlanıyor:', calculatedRecipientId);
      
      if (isNaN(parseInt(calculatedRecipientId))) {
        console.error('Geçersiz Recipient ID:', calculatedRecipientId);
        return;
      }
      
      setRecipientId(calculatedRecipientId);
      setIsChatOpen(true);
    }
  }, [location.state, project]);

  useEffect(() => {
    if (project?.customer_id) {
      const validRecipientId = project.customer_id.toString();
      console.log('RecipientID Ayarlanıyor:', validRecipientId);
      setRecipientId(validRecipientId);
      setIsChatOpen(true);
    }
  }, [project, location.state?.openChat]);

  console.log('Proje Sahibi ID:', project?.customer_id);

  if (loading) return <CircularProgress />;
  if (error) return <Typography color="error">{error}</Typography>;
  if (!project) return null;

  return (
    <Card sx={{ maxWidth: 800, margin: 'auto', mt: 4 }}>
      <CardContent>
        {/* Kategori */}
        <Chip 
          label={project.category}
          size="small"
          color="primary"
          sx={{ mb: 2 }}
        />
        {/* Başlık */}
        <Typography variant="h4" gutterBottom>
          {project.title}
        </Typography>
        {/* Açıklama */}
        <Typography variant="body1" gutterBottom>
          {project.description}
        </Typography>
        {/* Bütçe */}
        <Typography variant="body2" gutterBottom>
          <strong>Bütçe:</strong> {project.budget.toLocaleString('tr-TR')} ₺
        </Typography>
        {/* Teslim Tarihi */}
        <Typography variant="body2" gutterBottom>
          <strong>Teslim Tarihi:</strong> {new Date(project.deadline).toLocaleDateString('tr-TR')}
        </Typography>
        {/* Durum */}
        <Typography variant="body2" gutterBottom>
          <strong>Durum:</strong> {project.status === 'open' ? 'Açık' :
           project.status === 'in_progress' ? 'Devam Ediyor' :
           project.status === 'completed' ? 'Tamamlandı' : project.status}
        </Typography>
        {/* Oluşturulma Tarihi */}
        <Typography variant="body2" gutterBottom>
          <strong>Oluşturulma Tarihi:</strong> {new Date(project.created_at).toLocaleDateString('tr-TR')}
        </Typography>
        {/* Müşteri Bilgileri */}
        <Box sx={{ display: 'flex', alignItems: 'center', mt: 2 }}>
          <Avatar 
            src={project.customer_image}
            sx={{ width: 40, height: 40, mr: 2 }}
          >
            {project.customer_name?.[0]}
          </Avatar>
          <Typography variant="body2">
            {project.customer_name}
          </Typography>
        </Box>
        {/* Geri Dön Butonu */}
        <Box sx={{ mt: 3 }}>
          <Button variant="contained" onClick={() => navigate(-1)}>
            Geri Dön
          </Button>
        </Box>
        <div className="project-actions">
          <Button 
            variant="contained" 
            onClick={() => setIsChatOpen(true)}
            startIcon={<MessageIcon />}
            sx={{ mt: 2 }}
          >
            Mesaj Gönder
          </Button>

          <ChatDialog
            open={isChatOpen}
            onClose={() => setIsChatOpen(false)}
            projectId={id || ''}
            recipientId={recipientId}
          />
        </div>
      </CardContent>
    </Card>
  );
};

export default ProjectDetail;
