import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import api from '../utils/axios';

interface Message {
    _id: string;
    conversation_id: number;
    sender: {
        _id: string;
        name: string;
        email: string;
    };
    receiver_id: number;
    content: string;
    project_id: number;
    status: 'sent' | 'delivered' | 'read';
    read: boolean;
    createdAt: string;
}

interface SendMessageOptions {
    onSuccess?: () => void;
}

export function useMessages(projectId: string, recipientId: string) {
    const queryClient = useQueryClient();

    const { data: messages, isLoading } = useQuery<Message[]>({
        queryKey: ['messages', projectId],
        queryFn: () => api.get(`/messages/project/${projectId}`).then(res => res.data.data)
    });

    const sendMessage = useMutation<Message, Error, string, unknown>({
        mutationFn: (content: string) => 
            api.post('/messages/send', {
                project_id: projectId,
                content,
                receiver_id: recipientId
            }).then(res => res.data.data),
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['messages', projectId] });
        }
    });

    const handleSendMessage = (content: string, options?: SendMessageOptions) => {
        sendMessage.mutate(content, {
            onSuccess: () => {
                options?.onSuccess?.();
            }
        });
    };

    return {
        messages,
        isLoading,
        sendMessage: handleSendMessage
    };
}
