import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import api from '../utils/axios';
import { ChatMessage } from '../types/chat';

interface Message {
    _id: string;
    conversation_id: number;
    sender: {
        _id: string;
        name: string;
        email: string;
    };
    receiver_id: number;
    content: string;
    project_id: number;
    status: 'sent' | 'delivered' | 'read';
    read: boolean;
    createdAt: string;
}

export function useMessages(projectId: string, recipientId: string) {
    const queryClient = useQueryClient();

    const { data: messages, isLoading, error } = useQuery<ChatMessage[]>({
        queryKey: ['messages', projectId],
        queryFn: async () => {
            try {
                const response = await api.get(`/messages/project/${projectId}`);
                
                if (!response.data.success) {
                    throw new Error(response.data.message);
                }

                return response.data.data.map((msg: any) => ({
                    id: msg.id,
                    content: msg.message_content || msg.message || msg.content,
                    createdAt: msg.created_at,
                    senderName: msg.sender_name,
                    isSender: msg.sender_id === parseInt(localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')!).id : '0')
                }));
            } catch (error) {
                console.error('Mesaj getirme hatası:', error);
                throw error;
            }
        },
        enabled: Boolean(projectId && localStorage.getItem('token')), // Token kontrolü ekle
        retry: 1 // Hata durumunda sadece 1 kez dene
    });

    const sendMessage = useMutation<Message, Error, string, unknown>({
        mutationFn: async (content: string) => {
            if (!recipientId || !projectId) {
                throw new Error('Gerekli bilgiler eksik');
            }
    
            console.log('Mesaj gönderiliyor:', {
                content,
                projectId,
                recipientId
            });
    
            // Anahtar isimlerini backend'in beklediği şekilde düzenliyoruz:
            const response = await api.post('/messages', {
                content,
                projectId: parseInt(projectId),
                recipientId: parseInt(recipientId)
            });
    
            if (!response.data.success) {
                throw new Error(response.data.message);
            }
    
            return response.data.data;
        },
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['messages', projectId] });
        }
    });

    return {
        messages: messages || [], // Boş dizi varsayılan değer olarak
        isLoading,
        error,
        sendMessage: (content: string) => sendMessage.mutate(content)
    };
}
