import { useState, useRef, useEffect } from 'react';
import {
  Paper,
  TextField,
  IconButton,
  Box,
  Typography,
  Stack
} from '@mui/material';
import { Send as SendIcon, Close as CloseIcon, Remove as MinimizeIcon } from '@mui/icons-material';
import { useQuery, useMutation } from '@tanstack/react-query';
import { ChatMessage } from '../types/chat';
import Draggable from 'react-draggable';

interface ChatDialogProps {
  open: boolean;
  onClose: () => void;
  projectId: string;
  recipientId: string;
}

function ChatDialog({ open, onClose, projectId, recipientId }: ChatDialogProps) {
  const [newMessage, setNewMessage] = useState('');
  const [isMinimized, setIsMinimized] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  
  // Mesajları çek
  const { data: messages = [], refetch } = useQuery({
    queryKey: ['messages', projectId, recipientId],
    queryFn: async () => {
      const response = await fetch(`/api/messages/${projectId}/${recipientId}`);
      if (!response.ok) throw new Error('Mesajlar yüklenemedi');
      return response.json();
    },
  });

  // Yeni mesaj gönder
  const { mutate: sendMessage } = useMutation({
    mutationFn: async (content: string) => {
      const response = await fetch('/api/messages', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ projectId, recipientId, content }),
      });
      if (!response.ok) throw new Error('Mesaj gönderilemedi');
      return response.json();
    },
    onSuccess: () => {
      setNewMessage('');
      refetch();
    },
  });

  // Otomatik scroll
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = (e: React.FormEvent) => {
    e.preventDefault();
    if (newMessage.trim()) {
      sendMessage(newMessage);
    }
  };

  if (!open) return null;

  return (
    <Draggable handle=".chat-header">
      <Paper
        sx={{
          position: 'fixed',
          bottom: 0,
          right: 30,
          width: 300,
          height: isMinimized ? '40px' : '400px',
          display: 'flex',
          flexDirection: 'column',
          boxShadow: 3,
          zIndex: 1000,
        }}
      >
        {/* Sohbet Başlığı */}
        <Box
          className="chat-header"
          sx={{
            p: 1,
            bgcolor: 'primary.main',
            color: 'white',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            cursor: 'move',
          }}
        >
          <Typography variant="subtitle2">Mesajlar</Typography>
          <Box>
            <IconButton size="small" onClick={() => setIsMinimized(!isMinimized)} sx={{ color: 'white' }}>
              <MinimizeIcon />
            </IconButton>
            <IconButton size="small" onClick={onClose} sx={{ color: 'white' }}>
              <CloseIcon />
            </IconButton>
          </Box>
        </Box>

        {!isMinimized && (
          <>
            {/* Mesaj Listesi */}
            <Box sx={{ flexGrow: 1, overflow: 'auto', p: 1 }}>
              <Stack spacing={1}>
                {messages.map((message: ChatMessage) => (
                  <Box
                    key={message.id}
                    sx={{
                      display: 'flex',
                      justifyContent: message.isSender ? 'flex-end' : 'flex-start',
                    }}
                  >
                    <Paper
                      sx={{
                        p: 1,
                        maxWidth: '70%',
                        bgcolor: message.isSender ? 'primary.main' : 'grey.100',
                        color: message.isSender ? 'white' : 'text.primary',
                        borderRadius: 2,
                      }}
                    >
                      <Typography variant="body2">{message.content}</Typography>
                      <Typography variant="caption" sx={{ opacity: 0.7 }}>
                        {new Date(message.createdAt).toLocaleTimeString()}
                      </Typography>
                    </Paper>
                  </Box>
                ))}
                <div ref={messagesEndRef} />
              </Stack>
            </Box>

            {/* Mesaj Gönderme Formu */}
            <Box 
              component="form" 
              onSubmit={handleSend} 
              sx={{ 
                p: 1, 
                display: 'flex', 
                gap: 1, 
                borderTop: 1, 
                borderColor: 'divider' 
              }}
            >
              <TextField
                fullWidth
                size="small"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Mesajınızı yazın..."
                variant="outlined"
              />
              <IconButton 
                color="primary" 
                type="submit" 
                disabled={!newMessage.trim()}
              >
                <SendIcon />
              </IconButton>
            </Box>
          </>
        )}
      </Paper>
    </Draggable>
  );
}

export default ChatDialog;
