import { useState, useRef, useEffect } from 'react';
import {
  Paper,
  TextField,
  IconButton,
  Box,
  Typography,
  Stack
} from '@mui/material';
import { Send as SendIcon, Close as CloseIcon, Remove as MinimizeIcon } from '@mui/icons-material';
import { useMessages } from '../hooks/useMessages';
import Draggable from 'react-draggable';
import { ChatMessage } from '../types/chat';
import api from '../utils/axios';
import { useSocket } from '../hooks/useSocket';

interface ChatDialogProps {
  open: boolean;
  onClose: () => void;
  projectId: string;
}

interface LocalUser {
  id: string; // veya number
  // diÄŸer alanlar...
}

function ChatDialog({ open, onClose, projectId }: ChatDialogProps) {
  const [recipientId, setRecipientId] = useState<string>('');
  const [newMessage, setNewMessage] = useState('');
  const [isMinimized, setIsMinimized] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const nodeRef = useRef<HTMLDivElement>(null);
  const socketRef = useSocket(projectId);
  
  const { isLoading } = useMessages(projectId, recipientId);
  const [messages, setMessages] = useState<ChatMessage[]>([]);

  // Otomatik scroll
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    const fetchProjectDetails = async () => {
      try {
        const userStr = localStorage.getItem('user');
        if (!userStr) throw new Error('User not found');
        
        const user = JSON.parse(userStr) as LocalUser;
        const currentUserId = parseInt(user.id, 10);
    
        const projectResponse = await api.get(`/projects/${projectId}`);
        const project = projectResponse.data.project;
    
        if (!project) throw new Error('Project not found');

        // Ensure customer_id and creator_id are parsed as numbers
        const customerId = parseInt(project.customer_id);
        const creatorId = parseInt(project.creator_id);
    
        // Determine recipient ID based on numeric comparison
        const otherUserId = customerId === currentUserId ? creatorId : customerId;

        console.log('MesajlaÅŸma ayarlarÄ±:', {
          currentUserId,
          customerId,
          creatorId,
          otherUserId
        });
    
        setRecipientId(otherUserId.toString());
      } catch (error) {
        console.error('Error fetching project:', error);
      }
    };

    if (open && projectId) {
      fetchProjectDetails();
    }
  }, [open, projectId]);

  useEffect(() => {
    const currentSocket = socketRef.current;
    if (!currentSocket) return;

    // Yeni mesaj dinleyicisi
    currentSocket.on('newMessage', (message: ChatMessage) => {
      setMessages(prev => [...prev, message]);
      scrollToBottom();
    });

    // Hata dinleyicisi
    currentSocket.on('error', (error) => {
      console.error('Socket hatasÄ±:', error);
      // Burada bir hata bildirimi gÃ¶sterebilirsiniz
    });

    return () => {
      currentSocket.off('newMessage');
      currentSocket.off('error');
    };
  }, [socketRef]);

  const handleSend = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!newMessage.trim()) return;

    try {
      const userStr = localStorage.getItem('user');
      if (!userStr) {
        console.error('KullanÄ±cÄ± bilgisi bulunamadÄ±');
        return;
      }

      const user = JSON.parse(userStr) as LocalUser;
      const senderId = parseInt(user.id, 10);

      socketRef.current?.emit('chatMessage', {
        senderId,
        receiverId: parseInt(recipientId),
        content: newMessage,
        projectId
      });

      setNewMessage('');
      scrollToBottom();
    } catch (error) {
      console.error('Mesaj gÃ¶nderme hatasÄ±:', error);
    }
  };

  if (!open) return null;

  return (
    <Draggable 
      handle=".chat-header" 
      nodeRef={nodeRef}
      bounds="parent"
      defaultPosition={{ x: 0, y: 0 }}
    >
      <Paper
        ref={nodeRef}
        elevation={3}
        sx={{
          position: 'fixed',
          bottom: 0,
          right: 30,
          width: 300,
          height: isMinimized ? '40px' : '400px',
          display: 'flex',
          flexDirection: 'column',
          boxShadow: 3,
          zIndex: 1000,
          backgroundColor: 'background.paper'
        }}
      >
        {/* Sohbet BaÅŸlÄ±ÄŸÄ± */}
        <Box
          className="chat-header"
          sx={{
            p: 1,
            bgcolor: 'primary.main',
            color: 'white',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            cursor: 'move',
          }}
        >
          <Typography variant="subtitle2">Mesajlar</Typography>
          <Box>
            <IconButton size="small" onClick={() => setIsMinimized(!isMinimized)} sx={{ color: 'white' }}>
              <MinimizeIcon />
            </IconButton>
            <IconButton size="small" onClick={onClose} sx={{ color: 'white' }}>
              <CloseIcon />
            </IconButton>
          </Box>
        </Box>

        {!isMinimized && (
          <>
            {/* Mesaj Listesi */}
            {messages.map((message) => (
            <Box 
                key={message.id} // ðŸ‘ˆ Buraya unique deÄŸer ekleyin
                sx={{ 
                display: 'flex',
                justifyContent: message.isSender ? 'flex-end' : 'flex-start',
                mb: 2
                }}
            >
              <Stack spacing={1}>
                {isLoading ? (
                  <Typography variant="body2" align="center">Mesajlar yÃ¼kleniyor...</Typography>
                ) : (
                  messages?.map((message: ChatMessage) => (
                    <Box
                      key={message.id}
                      sx={{
                        display: 'flex',
                        justifyContent: message.isSender ? 'flex-end' : 'flex-start',
                      }}
                    >
                      <Paper
                        sx={{
                          p: 1,
                          maxWidth: '70%',
                          bgcolor: message.isSender ? 'primary.main' : 'grey.100',
                          color: message.isSender ? 'white' : 'text.primary',
                          borderRadius: 2,
                        }}
                      >
                        <Typography variant="body2">{message.content}</Typography>
                        <Typography variant="caption" sx={{ opacity: 0.7 }}>
                          {new Date(message.created_at).toLocaleTimeString()}
                        </Typography>
                      </Paper>
                    </Box>
                  ))
                )}
                <div ref={messagesEndRef} />
              </Stack>
            </Box>
            ))}

            {/* Mesaj GÃ¶nderme Formu */}
            <Box 
              component="form" 
              onSubmit={handleSend} 
              sx={{ 
                p: 1, 
                display: 'flex', 
                gap: 1, 
                borderTop: 1, 
                borderColor: 'divider' 
              }}
            >
              <TextField
                fullWidth
                size="small"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="MesajÄ±nÄ±zÄ± yazÄ±n..."
                variant="outlined"
              />
              <IconButton 
                color="primary" 
                type="submit" 
                disabled={!newMessage.trim()}
              >
                <SendIcon />
              </IconButton>
            </Box>
          </>
        )}
      </Paper>
    </Draggable>
  );
}

export default ChatDialog;
