import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { Box, Typography, Card, CardContent, Button, Avatar, Chip } from '@mui/material';
import api from '../utils/axios';
import { MessageOutlined as MessageIcon } from '@mui/icons-material';
import ChatDialog from '../components/ChatDialog';

interface Project {
  id: number;
  title: string;
  description: string;
  budget: number;
  deadline: string;
  category: string;
  customer_name: string;
  customer_image?: string;
  status: string;
  created_at: string;
  userId: string;
  customer_id: string;
}

const ProjectDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [project, setProject] = useState<Project | null>(null);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();
  const [isChatOpen, setIsChatOpen] = useState(false);
  const location = useLocation();
  const [recipientId, setRecipientId] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchProject = async () => {
      if (!id) return;
      try {
        setLoading(true);
        const response = await api.get(`/projects/${id}`);
        
        // API yanıtını kontrol et
        if (!response.data?.success || !response.data.project) {
          throw new Error(response.data.message || 'Proje yüklenemedi');
        }

        const projectData = response.data.project;
        
        // Müşteri ID kontrolü
        if (!projectData.customer_id) {
          throw new Error('Proje sahibi bilgisi eksik');
        }

        setProject({
          ...projectData,
          budget: Number(projectData.budget),
          deadline: new Date(projectData.deadline),
          created_at: new Date(projectData.created_at)
        });

      } catch (err) {
        console.error('Proje yükleme hatası:', err);
        navigate('/projects');
      } finally {
        setLoading(false);
      }
    };

    if (id && /^\d+$/.test(id)) { // ID'nin sayısal olduğunu kontrol et
      fetchProject();
    } else {
      setError('Geçersiz proje ID');
    }
  }, [id, navigate]);

  useEffect(() => {
    if (project?.customer_id) {
      const validRecipientId = project.customer_id.toString();
      if (validRecipientId && validRecipientId !== recipientId) {
        console.log('RecipientID Güncellendi:', validRecipientId);
        setRecipientId(validRecipientId);
      }
    }
  }, [project, recipientId]);

  useEffect(() => {
    if (location.state?.openChat) {
      setIsChatOpen(true);
    }
  }, [location.state?.openChat]);

  console.log('Proje Verisi:', {
    id: project?.id,
    customerId: project?.customer_id,
    rawData: project
  });

  if (error) return <Typography color="error">{error}</Typography>;
  if (!project) return null;

  return (
    <Card sx={{ maxWidth: 800, margin: 'auto', mt: 4 }}>
      <CardContent>
        {/* Kategori */}
        <Chip 
          label={project.category}
          size="small"
          color="primary"
          sx={{ mb: 2 }}
        />
        {/* Başlık */}
        <Typography variant="h4" gutterBottom>
          {project.title}
        </Typography>
        {/* Açıklama */}
        <Typography variant="body1" gutterBottom>
          {project.description}
        </Typography>
        {/* Bütçe */}
        <Typography variant="body2" gutterBottom>
          <strong>Bütçe:</strong> {project.budget.toLocaleString('tr-TR')} ₺
        </Typography>
        {/* Teslim Tarihi */}
        <Typography variant="body2" gutterBottom>
          <strong>Teslim Tarihi:</strong> {new Date(project.deadline).toLocaleDateString('tr-TR')}
        </Typography>
        {/* Durum */}
        <Typography variant="body2" gutterBottom>
          <strong>Durum:</strong> {project.status === 'open' ? 'Açık' :
           project.status === 'in_progress' ? 'Devam Ediyor' :
           project.status === 'completed' ? 'Tamamlandı' : project.status}
        </Typography>
        {/* Oluşturulma Tarihi */}
        <Typography variant="body2" gutterBottom>
          <strong>Oluşturulma Tarihi:</strong> {new Date(project.created_at).toLocaleDateString('tr-TR')}
        </Typography>
        {/* Müşteri Bilgileri */}
        <Box sx={{ display: 'flex', alignItems: 'center', mt: 2 }}>
          <Avatar 
            src={project.customer_image}
            sx={{ width: 40, height: 40, mr: 2 }}
          >
            {project.customer_name?.[0]}
          </Avatar>
          <Typography variant="body2">
            {project.customer_name}
          </Typography>
        </Box>
        {/* Geri Dön Butonu */}
        <Box sx={{ mt: 3 }}>
          <Button variant="contained" onClick={() => navigate(-1)}>
            Geri Dön
          </Button>
        </Box>
        <div className="project-actions">
          <Button 
            variant="contained" 
            onClick={() => setIsChatOpen(true)}
            startIcon={<MessageIcon />}
            sx={{ mt: 2 }}
          >
            Mesaj Gönder
          </Button>

          <ChatDialog
            open={isChatOpen}
            onClose={() => setIsChatOpen(false)}
            projectId={id || ''}
            recipientId={recipientId}
          />
        </div>
      </CardContent>
    </Card>
  );
};

export default ProjectDetail;
