import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import api from '../utils/axios';
import { ChatMessage } from '../types/chat';

export function useMessages(projectId: string, recipientId: string) {
    const queryClient = useQueryClient();

    const { data: messages, isLoading, error } = useQuery<ChatMessage[]>({
        queryKey: ['messages', projectId],
        queryFn: async () => {
            try {
                const response = await api.get(`/api/messages/project/${projectId}`);
                
                if (!response.data.success) {
                    throw new Error(response.data.message);
                }

                // Backend'den gelen mesaj yapısına uygun dönüşüm
                return response.data.messages.map((msg: any) => ({
                    id: msg.id,
                    content: msg.content,
                    created_at: msg.created_at,
                    sender_id: msg.sender_id,
                    receiver_id: msg.recipient_id,
                    project_id: msg.project_id,
                    isSender: msg.is_sender
                }));
            } catch (error) {
                console.error('Mesaj getirme hatası:', error);
                throw error;
            }
        },
        enabled: Boolean(projectId && localStorage.getItem('token'))
    });

    const sendMessage = useMutation({
        mutationFn: async (content: string) => {
            const response = await api.post('/api/messages', {
                content,
                projectId: parseInt(projectId),
                recipientId: parseInt(recipientId)
            });

            if (!response.data.success) {
                throw new Error(response.data.message);
            }

            return response.data.data;
        },
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['messages', projectId] });
        }
    });

    return {
        messages: messages || [],
        isLoading,
        error,
        sendMessage: (content: string) => sendMessage.mutate(content)
    };
}
