import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import api from '../utils/axios';
import { ChatMessage } from '../types/chat';

interface Message {
    _id: string;
    conversation_id: number;
    sender: {
        _id: string;
        name: string;
        email: string;
    };
    receiver_id: number;
    content: string;
    project_id: number;
    status: 'sent' | 'delivered' | 'read';
    read: boolean;
    createdAt: string;
}

interface SendMessageOptions {
    onSuccess?: () => void;
}

export function useMessages(projectId: string, recipientId: string) {
    const queryClient = useQueryClient();

    const { data: messages, isLoading } = useQuery<ChatMessage[]>({
        queryKey: ['messages', projectId],
        queryFn: async () => {
            const response = await api.get(`/messages/project/${projectId}`);
            const messages = response.data.data;
            // Message'ları ChatMessage formatına dönüştür
            return messages.map((msg: Message) => ({
                id: msg._id,
                content: msg.content,
                createdAt: msg.createdAt,
                isSender: msg.sender._id === localStorage.getItem('userId')
            }));
        }
    });

    const sendMessage = useMutation<Message, Error, string, unknown>({
        mutationFn: async (content: string) => {
            const response = await api.post('/api/messages', {
                content,
                projectId,
                recipientId
            });
            
            if (!response.data.success) {
                throw new Error(response.data.message || 'Mesaj gönderilemedi');
            }
            
            return response.data.data;
        },
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['messages', projectId] });
        }
    });

    const handleSendMessage = (content: string, options?: SendMessageOptions) => {
        sendMessage.mutate(content, {
            onSuccess: () => {
                options?.onSuccess?.();
            }
        });
    };

    return {
        messages,
        isLoading,
        sendMessage: handleSendMessage
    };
}
