import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:3000/api',  // baseURL doğru
    headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
    }
});

// CORS sorununu çözmek için headers ekleyelim
api.interceptors.request.use((config) => {
    const token = localStorage.getItem('token');
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    // CORS headers
    config.headers['Access-Control-Allow-Origin'] = 'http://localhost:3001';
    config.headers['Access-Control-Allow-Methods'] = 'GET, POST, PUT, DELETE, OPTIONS';
    config.headers['Access-Control-Allow-Headers'] = 'Origin, Content-Type, Accept, Authorization';
    
    return config;
});

export default api;
