import React, { useState, useEffect } from 'react';
import {
  Badge,
  IconButton,
  Menu,
  MenuItem,
  Typography,
  Box,
  Divider,
  Avatar
} from '@mui/material';
import { Mail as MailIcon } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { useSocket } from '../hooks/useSocket';
import api from '../utils/axios';

interface Message {
  id: string;
  content: string;
  sender_name: string;
  project_id: number;
  created_at: string;
  read: boolean;
}

export function NotificationBadge() {
  const [unreadCount, setUnreadCount] = useState(0);
  const [messages, setMessages] = useState<Message[]>([]);
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const navigate = useNavigate();
  const socket = useSocket();

  useEffect(() => {
    fetchUnreadMessages();
    
    const currentSocket = socket.current;
    
    currentSocket?.on('newMessage', () => {
        fetchUnreadMessages();
    });

    return () => {
        currentSocket?.off('newMessage');
    };
  }, [socket]);

  const fetchUnreadMessages = async () => {
    try {
      const response = await api.get('/messages/unread');
      setMessages(response.data.data);
      setUnreadCount(response.data.data.length);
    } catch (error) {
      console.error('Okunmamış mesajlar alınamadı:', error);
    }
  };

  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleMessageClick = async (message: Message) => {
    try {
      // Mesajı okundu olarak işaretle
      await api.put(`/messages/${message.id}/read`);
      
      // Projeye git
      navigate(`/projects/${message.project_id}`);
      handleClose();
      
      // Listeyi güncelle
      fetchUnreadMessages();
    } catch (error) {
      console.error('Mesaj işaretlenemedi:', error);
    }
  };

  return (
    <>
      <IconButton color="inherit" onClick={handleClick}>
        <Badge badgeContent={unreadCount} color="error">
          <MailIcon />
        </Badge>
      </IconButton>

      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleClose}
        PaperProps={{
          sx: { width: 320, maxHeight: 400 }
        }}
      >
        <Box sx={{ p: 2 }}>
          <Typography variant="h6">Mesajlar</Typography>
        </Box>
        <Divider />
        
        {messages.length === 0 ? (
          <MenuItem disabled>
            <Typography variant="body2">Yeni mesaj yok</Typography>
          </MenuItem>
        ) : (
          messages.map((message) => (
            <MenuItem 
              key={message.id} 
              onClick={() => handleMessageClick(message)}
              sx={{ 
                py: 1.5,
                px: 2,
                borderBottom: '1px solid',
                borderColor: 'divider'
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'flex-start', gap: 1 }}>
                <Avatar sx={{ width: 32, height: 32 }}>
                  {message.sender_name[0]}
                </Avatar>
                <Box>
                  <Typography variant="subtitle2">
                    {message.sender_name}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" noWrap>
                    {message.content}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    {new Date(message.created_at).toLocaleString('tr-TR')}
                  </Typography>
                </Box>
              </Box>
            </MenuItem>
          ))
        )}
      </Menu>
    </>
  );
}
